import React, {Component, useState, useEffect} from "react";
import service from "../services/service";
import {FormControl, InputLabel, MenuItem, Select} from "@material-ui/core";

class FilterbarComponent extends Component{

    constructor(props) {
        super(props);
        this.state = {
            brandoptions : [],
            selectedOption: null,
        }
    }
    componentDidMount() {
        console.log("FilterbarComponent componentDidmount fired");
        service.getBrands().then( (res) =>{
            console.log("FilterbarComponent, :", res);
            this.setState({brandoptions: res.data});
        });
    };

    render() {
        console.log("FilterbarComponent render: ", this.state.brandoptions);
        const handleChange = (event) => {
            this.state.selectedOption = evet.target.value;
        };

        return(
            <div className="container">
                <FormControl sx={{m:1, minWidth: 300}}>
                    <InputLabel>브랜드</InputLabel>
                    <Select onChange={handleChange} label="브랜드" id="demo-simple-select-helper" value={this.state.selectedOption}
                    MenuProps={{PaperProps:{sx:{maxHeight: {xs:300, sm:100}}}}}>
                        {this.state.brandoptions.map( (option, index) => (
                            <MenuItem key={option}>{option}</MenuItem>
                        ))}
                    </Select>
                </FormControl>
            </div>
        );
    }
}
export default FilterbarComponent
